# 1) run the following code to create a 'data' folder on the working directory# and a connection to the folder via the dbConnect functionif(!dir.exists('data')) dir.create('data')library(RMySQL)con <- dbConnect(RSQLite::SQLite(), 'data/example.sqlite')# 2) copy the mpg dataset from the ggplot2 package to the data folderlibrary(ggplot2)dbWriteTable(con, "mpg", as.data.frame(mpg), row.names(F))# 3) check which tables are present in the data folderdbListTables(con)# 4) select all cases from the datasetdbGetQuery(con, "select * from mpg")# 5) select the model, year, cyl variables for the toyota branddbGetQuery(con, "select model, year, cyl from mpg where manufacturer = 'toyota'")# 6) select all variables for toyota corolla casesdbGetQuery(con, "select model, year, cyl from mpg where manufacturer = 'toyota' and model = 'corolla' ")# 7) copy the diamonds dataset from the ggplot2 package into the data folder and check the tables in the databasedbWriteTable(con, 'diamonds', as.data.frame(diamonds), row.names=F)dbListTables(con)# 8) disconnect databasedbDisconnect(con)# 9) load the df1.csv dataset with basic functions and with data.table# to see the difference in loading timeslibrary(data.table)system.time(df_base <- read.csv("df1.csv"))system.time(df_fread <- fread("df1.csv"))# 10) convert the diamonds dataset to data table formatlibrary(ggplot2)dt1 <- data.table(diamonds)# 11) extract the first columndt1[, 1]dt1[, carat]dt1[, .(carat)]# 12) extract the x, y and z columndt1[ , list(x, y, z)]dt1[ , .(x, y, z)]# 13) calculate the average price based on the type of cutdt1[, mean(price), by = cut]# 14) extract cases with color equal to E and depth value greater than 60dt1[dt1$color == 'E' & dt1$depth > 60]# 15) add the price_in_euro variabledt1$price_euro <- dt1$price * 0.92